targets.query:
from(bucket: "${Process_Bucket}")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "ElectricalMeasurement")
  |> filter(fn: (r) => r["SensorName"] == "BC1")
  |> filter(fn: (r) => r["_field"] == "DriveState")

  //|> map(fn: (r) => ( { r with DriveStatus: if r._value == "Running" then 1
  //                                          else if r._value == "Stopped" then 0
   //                                         else
   //                                             0
   //                     })  )

from(bucket: "${Process_Bucket}" )
  |> range(start: v.timeRangeStart, stop:v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "ElectricalMeasurement" and r.SensorName == "BC1")
  |> filter(fn: (r) => r._field == "L1_CurrIntegral_Anomaly")
  
 |> reduce(
             fn: (r, accumulator) => ( {
                                        sum_anom: r._value + accumulator.sum_anom, 
                                        count_anom: accumulator.count_anom + 1,
                                        ratio:  100.0 * float(v: r._value + accumulator.sum_anom ) / float (v: accumulator.count_anom + 1 ),
                                        } ),
             identity: {sum_anom: 0.0 , count_anom: 0, ratio: 0.0},
  )

|> drop (columns: ["sum_anom" , "count_anom"] )

